import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

const name = 'Hello Sir';
const currTime= new Date(2022,7,22,7).getHours();
let greeting='';
let cssStyle= {};

if(currTime>=1 && currTime<12){
  greeting='Good Morning';
  cssStyle.color='green';
}else if(currTime>=12 && currTime<19){
  greeting='Good Afternoon';
  cssStyle.color='orange';
}else{
  greeting='Good Night';
  cssStyle.color='black';
}


ReactDOM.render(
  < >
  <div style={{width:'100%', height:'100vh', backgroundColor:'aqua'}}>
  <h1 style= {{width:'500px', height:'100px',backgroundColor: 'yellow', margin: '400px 100px 300px 550px',padding:'40px 0px 0px 0px', textAlign:'center', display:'inline-block', borderRadius:'50px'}}> {name}, <span style={cssStyle}>{greeting}</span></h1>
  </div>

  </>,
  document.getElementById('root')

);
